import React, { useState, useMemo, useEffect, useRef } from 'react';
import TinderCard from 'react-tinder-card';

function Advanced() {
  const [songs, setSongs] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [lastDirection, setLastDirection] = useState();
  const currentIndexRef = useRef(currentIndex);
  const childRefs = useMemo(() => Array(songs.length).fill(0).map(() => React.createRef()), [songs.length]);

  useEffect(() => {
    checkLoginAndFetchData();
  }, []);

  const checkLoginAndFetchData = async () => {
    // This URL needs to point to your check_login backend endpoint
    const checkLoginUrl = 'http://localhost:8080/check';
    try {
      let response = await fetch(checkLoginUrl, { credentials: 'include', mode: 'cors' });
      if (response.ok) {
        let data = await response.json();
        if (data.status === 'success') {
          fetchDaylist();
        } else {
          // Redirect to Spotify login page or show a login button
          window.location.href = '/login'; // Adjust as necessary
        }
      } else {
        throw new Error('Failed to check login status');
      }
    } catch (error) {
      console.error('Error during login check:', error);
    }
  };

  const fetchDaylist = async () => {
    setIsLoading(true);
    try {
      // Adjust the URL to where your backend endpoint for getDaylist is hosted
      const response = await fetch('http://localhost:8080/getDaylist', { credentials: 'include', mode: 'cors' });
      if (!response.ok) throw new Error('Failed to fetch daylist');
      const data = await response.json();
      setSongs(data.song_info);
      setCurrentIndex(data.song_info.length - 1);
    } catch (error) {
      console.error('Error fetching daylist:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const updateCurrentIndex = (val) => {
    setCurrentIndex(val);
    currentIndexRef.current = val;
  };

  const canGoBack = currentIndex < songs.length - 1;
  const canSwipe = currentIndex >= 0;

  const swiped = (direction, nameToDelete, index) => {
    setLastDirection(direction);
    updateCurrentIndex(index - 1);
  };

  const outOfFrame = (name, idx) => {
    console.log(`${name} (${idx}) left the screen!`, currentIndexRef.current);
    currentIndexRef.current >= idx && childRefs[idx].current.restoreCard();
  };

  const swipe = async (dir) => {
    if (canSwipe && currentIndex < songs.length) {
      await childRefs[currentIndex].current.swipe(dir);
    }
  };

  const goBack = async () => {
    if (!canGoBack) return;
    const newIndex = currentIndex + 1;
    updateCurrentIndex(newIndex);
    await childRefs[newIndex].current.restoreCard();
  };

  return !isLoading ? (
    <div>
      {/* Existing layout and CSS links */}
      <h1>Spotify Tinder Cards</h1>
      <div className='cardContainer'>
        {songs.map((song, index) => (
          <TinderCard
            ref={childRefs[index]}
            className='swipe'
            key={song.song_name}
            onSwipe={(dir) => swiped(dir, song.song_name, index)}
            onCardLeftScreen={() => outOfFrame(song.song_name, index)}
          >
            <div
              style={{ backgroundImage: `url(${song.album_cover})` }}
              className='card'
            >
              <h3>{song.song_name}</h3>
              {/* Additional song details can be added here */}
            </div>
          </TinderCard>
        ))}
      </div>
    </div>
  ) : (
    <span> Loading... </span>
  );
}

export default Advanced;
